"use client"
import { useState } from "react"
import { TableField, type TableFieldData } from "@/registry/default/ui/table-field"

export default function TableFieldAddColumnDemo() {
  const [data, setData] = useState<TableFieldData>({
    columns: [
      { id: "name", label: "Name", type: "text" },
      { id: "email", label: "Email", type: "text" },
    ],
    rows: [
      { id: "1", data: { name: "John Doe", email: "john@example.com" } },
      { id: "2", data: { name: "Jane Smith", email: "jane@example.com" } },
    ],
  })
  return (
    <TableField
      value={data}
      onChange={setData}
      allowAddColumn={true}
      allowAddRow={false}
      allowDeleteColumn={true}
      allowDeleteRow={true}
      instructions="Add a new column to the right. Rows are fixed."
    />
  )
}
