"use client"
import { useState } from "react"
import { TableField, type TableFieldData } from "@/registry/default/ui/table-field"

export default function TableFieldAddRowDemo() {
  const [data, setData] = useState<TableFieldData>({
    columns: [
      { id: "name", label: "Name", type: "text" },
      { id: "email", label: "Email", type: "text" },
    ],
    rows: [
      { id: "1", data: { name: "John Doe", email: "john@example.com" } },
    ],
  })
  return (
    <TableField
      value={data}
      onChange={setData}
      allowAddColumn={false}
      allowAddRow={true}
      allowDeleteColumn={true}
      allowDeleteRow={true}
      instructions="Add a new row to the bottom. Columns are fixed."
    />
  )
}
