"use client"
import { useState } from "react"
import { TableField, type TableFieldData } from "@/registry/default/ui/table-field"

export default function TableFieldDynamicDemo() {
  const [data, setData] = useState<TableFieldData>({
    columns: [
      { id: "name", label: "Name", type: "text" },
    ],
    rows: [
      { id: "1", data: { name: "John Doe" } },
    ],
  })
  return (
    <TableField
      value={data}
      onChange={setData}
      allowAddColumn={true}
      allowAddRow={true}
      allowDeleteColumn={true}
      allowDeleteRow={true}
      instructions="Add both new rows and columns dynamically."
    />
  )
}
